import BlogList from "./BlogList";
import React, { useState, useEffect } from "react";
import mongoose from "mongoose";
//use effect is a hook that runs a function on every render of the component, e.g. to fetch data
//useState is a hook which rerenders the component on every state change

const Home = () => {
  const [blogs, setBlogs] = useState(null);
  const [isPending, setIsPending] = useState(true);
  const [error, setError] = useState(null);

   const uri =
    "mongodb+srv://tobias:eQjvxyS5Sh70yQt4@ReactBlog.mongodb.net/dbname?retryWrites=true&w=majority";

    try {
      mongoose.connect(uri, { useNewUrlParser: true, useUnifiedTopology: true });
    } catch (error) {
      console.error("Mongoose connection error:", error);
    }


  const UserSchema = new mongoose.Schema({
    name: String,
    age: Number
  });

  const UserModel = mongoose.model('User', UserSchema);

  const fetchUsers = async () => {
    try {
      const users = await UserModel.find();
      console.log(users);
    } catch (error) {
      console.error(error);
    }
  };

  fetchUsers();

  useEffect(() => {
    //use effect is a hook that runs a function on every render of the component, e.g. to fetch data
    //useState is a hook which rerenders the component on every state change
    setTimeout(() => {
      fetch("http://localhost:3000/db.js")
        .then((res) => {
          console.log(res);
          if (!res.ok) {
            throw Error("could not fetch the data");
          }
          return res.json();
        })
        .then((data) => {
          setBlogs(data);
          setIsPending(false);
          setError(null);
        })
        .catch((err) => {
          setIsPending(false);
          setError(err.message);
        });

      //fetch simulation
      setBlogs([
        {
          title: "My new website",
          body: "lorem ipsum...",
          author: "Tobi",
          id: 1,
        },
        {
          title: "My second site",
          body: "lorem ipsum...",
          author: "Dora",
          id: 2,
        },
        {
          title: "My third website",
          body: "lorem ipsum...",
          author: "Ida",
          id: 3,
        },
      ]);
      setIsPending(false);
    }, 1000);
    console.log("use effect ran");
  }, []);

  return (
    <div className="home">
      {error && <div>{error}</div>}
      {isPending && <div>Loading...</div>}
      {
        /* conditional templating with && --> checks left side first, is null at the beginning and therefore does not render the right side
       after useEffect run the side is rerendered and the blogs are set so blogs is true*/
        //Props is used to pass down the blogs from the parent to the child component called BlogList.js
      }
      {blogs && <BlogList blogs={blogs} title="All blogs!" />}
    </div>
  );
};

export default Home;
