import { useState } from "react";

const Create = () => {
  //state to keep track of the input values which can later be saved
  const [title, setTitle] = useState("dsf");

  const handleChange = (e) => {
    console.log("before setTitle: ", e);
    // () => setTitle()
    setTitle(e.target.value);
    console.log("after setTitle: ", e);
  };

  return (
    <div className="create">
      <h2>Add a new Blog</h2>
      <form>
        <label>Blog Title:</label>
        <input type="text" required value={title} onChange={setTitle} />
        <label>Content:</label>
        <textarea required></textarea>
        <label>Blog Author</label>
        <select>
          <option value="Dora">Dora</option>
          <option value="Ida">Ida</option>
        </select>
        <button>Add Blog</button>
      </form>
    </div>
  );
};

export default Create;
